import React from "react";
import Die from "./components/Die";

function App() {
  const min = 1
  const max = 6
  const allNewDice = () => {
    let newDice = []
    for (let i=0; i<10; i++){
      newDice[i] = Math.floor(Math.random() * (max - min) + min);
    }
    return newDice
  }

  const [dice, setDice] = React.useState(() => allNewDice())

  console.log(dice)

  return (
    <div className="App">
      <main>
        <div className="dice-container">
          {dice.map(dValue => {
            return (
              <Die value={dValue}/>
            )
          })}
        </div>
      </main>
    </div>
  );
}

export default App;
